import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Divider } from 'antd';
import 'antd/dist/antd.css';
const { Meta } = Card;

function StoryCard({key, data}){
    let storyDate = new Date(data.time * 1000).toLocaleString();

    return (
        <Link to={`/story/${data.id}`}>
            <Card 
                hoverable
                style={{
                    marginTop: 16,
                }}
                loading={false}
            >
                <Meta
                    title={data.title}
                />
                <span className="rating">Score: {data.score}</span>
                <Divider type="vertical" />
                <span className="author">By: {data.author}</span>
                <Divider type="vertical" />
                <span className="date">Date: {storyDate}</span>
            </Card>
        </Link>
    );
}

export default StoryCard;
