import React from 'react';
import { useParams } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { Card, Divider } from 'antd';
import 'antd/dist/antd.css';
import { uploadComments }  from "../store/actions"; 
import StoryComment from '../components/StoryComment';
const { Meta } = Card;

function StoryPage({}){
    const state = useSelector((state)=>state);
    const dispatch = useDispatch();

    const { id } = useParams();
    const story = state.find((item) => item.id == id);
    console.log(story);
    const { comments, setComments } = React.useState([]);
    let storyDate = new Date(story.time * 1000).toLocaleString();

    React.useEffect(() => {
        fetchComments();
    }, []);

    async function fetchComments() {
        try {
            let comments = [];
            for (let commentId of story.kids) {
                const commentResponse = await fetch(`https://hacker-news.firebaseio.com/v0/item/${commentId}.json?print=pretty`);
                let comment = await commentResponse.json();
                if (comment !== null) comments = [...comments, comment];
            }
            //dispatch(uploadComments({ storyId: storyId, comments: storyComments }));
            setComments(comments);
            console.log(comments);
        } catch (error) {
            alert('Ошибка при запросе данных');
        }
    }

    return (
        <div className="content">
            <Card
                style={{
                    marginTop: 16,
                }}
                loading={false}
            >
                <Meta
                    title={story.title}
                />
                <span className="url">URL: {story.url}</span>
                <Divider type="vertical" />
                <span className="rating">score: {story.score}</span>
                <Divider type="vertical" />
                <span className="author">by: {story.by}</span>
                <Divider type="vertical" />
                <span className="date">date: {storyDate}</span>
                <Divider type="vertical" />
                <span className="comments">comments: {story.kids? story.kids.length : 0}</span>
            </Card>
            <div className="comments">
                {
                    comments && 
                    comments.map((comment) => (
                        <StoryComment 
                            key = {comment.id}
                            data = {comment}
                        />
                    ))
                }           
            </div>
        </div>
    );
}

export default StoryPage;
